import React from "react";
import Img from "../slike/pozadina.jpg";
import Img1 from "../slike/ide.png";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import Perti from "./Perti";
import Particles from "react-particles";

const Home = () => {
  const style = {
    backgroundImage: `url(${Img})`,
  };
  const styled = {
    backgroundImage: `url(${Img1})`,
  };
  return (
    <div className="w-screen h-[calc(100%-70px)] bg-red absolute top-[70px] flex justify-center">
      <Particles
        options={{
          particles: {
            number: {
              value: 80,
              density: {
                enable: true,
                value_area: 800,
              },
            },
            color: {
              value: "#ffffff",
            },
            shape: {
              type: "circle",
              stroke: {
                width: 0,
                color: "#000000",
              },
              polygon: {
                nb_sides: 5,
              },
              image: {
                src: "img/github.svg",
                width: 100,
                height: 100,
              },
            },
            opacity: {
              value: 0.5,
              random: false,
              anim: {
                enable: false,
                speed: 1,
                opacity_min: 0.1,
                sync: false,
              },
            },
            size: {
              value: 10,
              random: true,
              anim: {
                enable: false,
                speed: 80,
                size_min: 0.1,
                sync: false,
              },
            },
            line_linked: {
              enable: true,
              distance: 300,
              color: "#ffffff",
              opacity: 0.4,
              width: 2,
            },
            move: {
              enable: true,
              speed: 12,
              direction: "none",
              random: false,
              straight: false,
              out_mode: "out",
              bounce: false,
              attract: {
                enable: false,
                rotateX: 600,
                rotateY: 1200,
              },
            },
          },
          interactivity: {
            detect_on: "canvas",
            events: {
              onhover: {
                enable: false,
                mode: "repulse",
              },
              onclick: {
                enable: true,
                mode: "push",
              },
              resize: true,
            },
            modes: {
              grab: {
                distance: 800,
                line_linked: {
                  opacity: 1,
                },
              },
              bubble: {
                distance: 800,
                size: 80,
                duration: 2,
                opacity: 8,
                speed: 3,
              },
              repulse: {
                distance: 400,
                duration: 0.4,
              },
              push: {
                particles_nb: 4,
              },
              remove: {
                particles_nb: 2,
              },
            },
          },
          retina_detect: true,
        }}
      ></Particles>
      <div className="w-[60%] h-full top-[70px]  items-center flex justify-center grid grid-cols-2">
        <motion.div
          className="h-full w-full  "
          initial={{ x: -300, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ type: "tween", duration: 0.7, ease: "easeInOut" }}
        >
          <div className="centar h-full w-full  ">
            <h1 className="text-white text-6xl">
              <span className="text-6xl ">
                {" "}
                Hi,
                <span className="text-6xl italic p-4">I'm Roberto</span>{" "}
                <br></br>
              </span>
            </h1>
            <h1 className="text-8xl font-bold bg-dark text-white">
              a web developer
            </h1>

            <h1 className="text-grey mb-2  ">React, Javascript, HTML, CSS</h1>
            <Link to="/aboutme">
              <button className="flex rounded-sm justify-start bg-yellow p-4 w-[150px] hover:w-full transition-all hover:bg-dark hover:text-white">
                About me
              </button>
            </Link>
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ type: "tween", duration: 0.7, ease: "easeInOut" }}
          className="h-full w-full  shadow-xl bato relative"
          style={styled}
        >
          <Link to="/projects">
            <div className="h-full w-full bg-black bg-opacity-30 absolute top-0 left-0 hover:bg-transparent cursor-pointer transiton-all group ">
              <div className="h-full w-full flex justify-end relative">
                <h1 className="text-2xl font-bold text-dark text-end absolute bottom-10 right-10 invisible bg-yellow w-[30%]  group-hover:visible  ">
                  My <br></br> <span>Projects</span>
                </h1>
              </div>
            </div>
          </Link>
        </motion.div>
      </div>
    </div>
  );
};

export default Home;
