import React from "react";
import Img from "../slike/pattern.png";
import Img1 from "../slike/ide.png";
import { Link } from "react-router-dom";

const Home = () => {
  const style = {
    backgroundImage: `url(${Img})`,
  };
  const styled = {
    backgroundImage: `url(${Img1})`,
  };
  return (
    <div
      className="w-screen h-[calc(100%-70px)] absolute top-[70px] bg-red flex justify-center"
      style={style}
    >
      <div className="w-[60%] h-full top-[70px]  items-center flex justify-center grid grid-cols-2">
        <div className="h-full w-full  ">
          <div className="centar h-full w-full ">
            <h1 className="text-white text-6xl">
              <span className="text-6xl ">
                {" "}
                Hi,
                <span className="text-4xl italic p-10">I'm Roberto</span>{" "}
                <br></br>
              </span>
              <span className="b border-yellow text-8xl font-bold">
                a web developer
              </span>
            </h1>

            <h1 className="text-grey mb-2  ">React, Javascript, HTML, CSS</h1>
            <Link to="/aboutme">
              <button className="flex rounded-sm justify-start bg-yellow p-4 w-[150px] hover:w-full transition-all hover:bg-dark hover:text-white">
                About me
              </button>
            </Link>
          </div>
        </div>

        <div
          className="h-full w-full bg-red shadow-xl bato relative"
          style={styled}
        >
          <div className="h-full w-full bg-black bg-opacity-30 absolute top-0 left-0 hover:bg-transparent cursor-pointer transiton-all group z-10">
            <div className="h-full w-full flex justify-end relative">
              <h1 className="text-2xl font-bold text-white text-end absolute bottom-10 right-10 z-[-1] group-hover:bg-dark group-hover:w-[80%] transition-all  ">
                My <br></br> <span>Projects</span>
              </h1>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
