import React from "react";
import Img from "../slike/pattern.png";

const Aboutme = () => {
  const style = {
    backgroundImage: `url(${Img})`,
  };
  return (
    <div
      className="w-screen h-[calc(100%-70px)] absolute top-[70px] bg-red flex justify-center"
      style={style}
    >
      <div className="h-full w-[60%] bg-red flex justify-center">
        <div className="h-full w-[80%] text-white centrar bg-yellow">
          <h1 className="text-5xl mb-2">Hi, my name is Roberto</h1>
          <h1 className="text-xl p-1">
            I'm a young self taught front end developer from Croatia. <br></br>
            Currently I'm looking for my first job. <br></br> At the moment, I'm
            very familiar with React, Javascript,HTML and CSS <br></br>
            But I'm looking forward to expand my lists of skills.
          </h1>
        </div>
      </div>
    </div>
  );
};

export default Aboutme;
