import React from "react";
import Img from "../slike/pattern.png";
import { FaBackspace } from "react-icons/fa";
import Img1 from "../slike/skills.png";

const Aboutme = () => {
  const style = {
    backgroundImage: `url(${Img})`,
  };
  const styled = {
    backgroundImage: `url(${Img1})`,
  };

  return (
    <div
      className="w-screen h-[calc(100%-70px)] absolute top-[70px] bg-red flex justify-center"
      style={style}
    >
      <div className="h-full w-[60%] bg-red flex justify-center ">
        <div className="h-full w-[80%] text-white centrar  relative">
          <button
            onClick={() => {
              window.location.href("/");
            }}
            className="absolute top-10 left-0 hover:text-dark hover:scale-125 transition-all"
          >
            {" "}
            <FaBackspace size={50} />
          </button>
          <h1 className="text-5xl mb-2 bg-dark">Hi, my name is Roberto</h1>
          <h1 className="text-xl p-1 bg-dark">
            I'm a young self taught front end developer from Croatia. <br></br>
            Currently, I'm looking for my first job. <br></br>
            I'm very familiar with React, Javascript, HTML and CSS, tailwind,
            framer-motion etc. <br></br>
            But I'm looking forward to expand my lists of skills as time goes on
          </h1>
        </div>
      </div>
    </div>
  );
};

export default Aboutme;
