import React from "react";
import Img from "../slike/pattern.png";

const Home = () => {
  const style = {
    backgroundImage: `url(${Img})`,
  };
  return (
    <div
      className="w-screen h-[calc(100%-70px)] absolute top-[70px] bg-red flex justify-center"
      style={style}
    >
      <div className="w-[60%] h-full top-[70px]  items-center flex justify-center grid grid-cols-2">
        <div className="h-full w-full bg-black bg-opacity-30 ">
          <div>
            <h1 className="text-white text-6xl">
              Hi,<br></br> I'm Roberto <br></br> a web developer
            </h1>
            <h1 className="text-grey">React, Javascript, HTML, CSS</h1>
            <button className="bg-yellow p-4 w-[150px]">About me</button>
          </div>
        </div>

        <div className="h-full w-full "></div>
      </div>
    </div>
  );
};

export default Home;
